"use strict";window.addEventListener('load',()=>{async function a(A,b,c,d,e){let f=document.getElementById(A),g=document.getElementById(c),h=document.getElementById(b);g.innerHTML=`
    <div class="animate-spin inline-block size-4 border-3 border-current border-t-transparent text-blue-600 rounded-full dark:text-blue-500" role="status" aria-label="${g.dataset.label}">
      <span class="sr-only">${g.dataset.label}</span>
    </div>`;let i=await fetch('/translate',{method:'POST',headers:{'Content-Type':'application/json; charset=utf-8'},body:JSON.stringify({text:f.innerText,src_lang:d,dest_lang:e})});let j=await i.json();h.innerText=j.text;h.parentElement.classList.toggle('hidden');g.innerHTML=''}document.getElementById('posts-container').addEventListener('click',_=>{if(!_.target.matches('.translate-link'))return;_.preventDefault();let B=_.target,C=B.dataset.postId;a(`post${C}`,`translation${C}`,`link${C}`,B.dataset.language,B.dataset.locale)})});
